mapper.select:
select * from stock
where 1=1 and item_type like '%식자재'

	and item_type = #{item_type}


	and item_num like CONCAT('%', #{item_num}, '%')


	and item_name like CONCAT('%',  #{item_name}, '%')


	and whs_num = #{whs_num}

 limit #{startRow}, #{pageSize}

select count(*) from stock
where 1=1

	and item_type = #{item_type}


	and item_num like CONCAT('%', #{item_num}, '%')


	and item_name like CONCAT('%', #{item_name}, '%')


	and whs_num = #{whs_num}
